mapper.select:
select ai.*,am.name makerName,ab.brand_name
from auto_info ai
left join auto_maker am on ai.maker_id=am.id
left join auto_brand ab on ai.brand_id=ab.id
where ai.deleted=0
and am.deleted=0
and ab.deleted=0

    and ai.auto_num like concat('%',#{autoInfo.autoNum},'%')


    and ai.maker_id=#{autoInfo.makerId}


    and ai.brand_id=#{autoInfo.brandId}


    and ai.info_type=#{autoInfo.infoType}


    and ai.status=#{autoInfo.status}


    and ai.registration_date >= #{autoInfo.lowRegistrationDate}


    and ai.registration_date <= #{autoInfo.highRegistrationDate}


    and ai.rent >= #{autoInfo.lowRent}


    and ai.rent <= #{autoInfo.highRent}
